const showGenericCard = game.pirateborg.api.showGenericCard;
const outcome = await game.pirateborg.api.outcomes.rollOutcome({
 formula: "5d6",
 data: actor.getRollData(), 
})();

outcome.title = outcome.roll.total % 2 ? 'Failure' : 'Success' 

await showGenericCard({
 actor: actor,
 title: item.name,
 description: item.description,
 outcomes: [outcome],
})
